import {Link} from 'react-router-dom'
import axios from 'axios';
import './product.css'
//import Productapp from './searchProduct.jsx'
const Product=()=>{
  const [datas,setDatas]=useState([]);
 //const [isLoading,setLoading]=useState(true);
  //const [error,setError]=useState(null);
  const url="http://localhost:3001/read"
   useEffect(()=>{
     axios.get(url,{withCredentials:true})
    .then((res)=>{
    setDatas(res.data.payload.findAll)
      //payload return user useState parse data 
    }).catch(err => alert(err.message))
  })
  return <div className="">
  <div className="search_container">
  <div><input className="" type="text" placeholder="search product" /></div>
  <div><button className="btn"> search</button></div>
  </div>
  <div className="ptoduct_container dispaly_grid">
  {
    datas?.map((todo)=>{
    const {id, image}=todo;
      return <article key={id} className="card bg-blue-100 ml-2">
<img className="" src={image} alt="" />
<div className="text_container">
<span className="bagde">store Ready</span>
<span className="bagde2">Offical Store</span>
<h1 className="bagde3">Best Product quality</h1>
<div className="price">
<b>Rp 400.00</b>
</div>
<div>*** rating</div>
</div>
<div className="grid grid-cols-2">
<p className="add_card">Add to Card</p>
<Link to="/card" className="bg-blue-700 text-white ml-2 text-sm">more product..</Link>
<p>min. order: 2 pice</p>
</div>
</article>
    })
  }
  </div>
  </div>
}
export default Product
