  const makeRequest=async(url)=>{
try{
  setLoading(true)
      const res= await axios.get(url,{withCredentials:true});
    setDatas(res.data.payload.findAll)
setLoading(false)
 setError(null)
}catch(error){
  setLoading(false)
    setError(error)
}
  }
  useEffect(()=>{
    makeRequest(url);
  })
