  </div>
    {isLoading && <h2> Data is Loading..</h2>}
    {error && <p>{error.message}</p>}
  <div className="ptoduct_container dispaly_grid">
  {
    datas?.map((todo)=>{
    const {id, image}=todo;
      return <article key={id} className="card bg-blue-100 ml-2">
